      if (textBuff.substring(begin,end).indexOf("String_Node_Str") != -1) {
        begin=textBuff.indexOf("String_Node_Str",begin) + 1;
        end=textBuff.indexOf("String_Node_Str",begin + 1);
        if (begin == 0 || end == -1) {
          break;
